            <div class=\"col-12\">
                <h1>List des produits de la catégorie : {{ category.Name }}</h1>
            </div>
            {% for product in products %}
                <div class=\"col-3 mb-3\">
                    <article class=\"card h-100\">
                        <div class=\"card-body\">
                        <h2 class=\"card-title\">{{ product.name }}</h2>
                        <p class=\"card-text\">{{ product.description }}</p>
